import React, { useState } from 'react';

export default function TextForm(props) {
    const [text, setText] = useState("");

    const handleOnChange = (e) => {
        setText(e.target.value)
    }

    const handleUpClick = () => {
        let newText = text.toUpperCase()
        setText(newText)
    }

    const handleloClick = () => {
        let newText = text.toLowerCase()
        setText(newText)
    }

    const handleCapitalized = () => {
        let newText = text.split(" ")
        for (let i = 0; i < newText.length; i++) {
            newText[i] = newText[i][0].toUpperCase() + newText[i].substring(1)
        }
        setText(newText.join(" "))
    }

    const handleAlternating = () => {
        let newText = ""
        for (let i = 0; i < text.length; i++) {
            if(text[i] === text[i].toLowerCase()){
                newText += text[i].toUpperCase()
            }else{
                newText += text[i].toLowerCase()
            }
        }
        setText(newText)
    }

    const handleClearClick = () => {
        setText("")
    }

    return (
        <div>
            <div className="container my-3">
                <h1> {props.heading} </h1>
                <div className="form-group my-2">
                    <textarea className="form-control" value={text} onChange={handleOnChange} placeholder='Enter text here' id="myForm" rows="5"></textarea>
                </div>
                <button className="btn btn-primary mx-2" onClick={handleUpClick}>Convert To Uppercase</button>
                <button className="btn btn-primary" onClick={handleloClick}>Convert To Lowercase</button>
                <button className="btn btn-primary mx-2" onClick={handleCapitalized}>Convert To Capitalized</button>
                <button className="btn btn-primary" onClick={handleAlternating}>Convert To Alternating</button>
                <button className="btn btn-primary mx-2" onClick={handleAlternating}>Convert To in</button>
                <button className="btn btn-primary" onClick={handleClearClick}>Clear Text</button>
            </div>

            <div className="container">
                <h2>analyse text</h2>
                <p>{text.split(" ").length} word and {text.length} text</p>
                <p>{text.split(" ").length * 0.008} minutes read </p>
            </div>
        </div>
    )
}
