import React, { useState } from 'react';

export default function TextForm(props) {
    const [text, setText] = useState("")
    // text = "new text"; // Wrong way to change the state
    // setText("new text"); // Correct way to change the state

    const handleUpClick = () => {
        let newText = text.toUpperCase();
        setText(newText)
    }

    const handlelwClick = () => {
        let newText = text.toLowerCase();
        setText(newText)
    }

    const handleOnChange = (e) => {
        setText(e.target.value)
    }

    return (
        <div>
            <div className="container my-3">
                <h2> {props.heading} </h2>
                <div className="mb-3">
                    <textarea className="form-control" value={text} onChange={handleOnChange} placeholder='Enter text here' id="myBox" rows="3"></textarea>
                </div>
                <button className="btn btn-primary mx-2" onClick={handleUpClick} >Conver To Uppercase</button>
                <button className="btn btn-primary" onClick={handlelwClick} >Conver To Lowercase</button>
            </div>

            <div className="container"></div>
        </div>
    )
}
